  const person =
   [{id:"1", name:"احمد",date:"5 مساءا",img:"person"},
  {id:"1", name:"محمد",date:"5 مساءا",img:"person"},
  {id:"1", name:"عبود",date:"6 مساءا",img:"person"},
  {id:"1", name:"عبود",date:"6 مساءا",img:"person"}]
  return (
    <div className="font color-body">
      <Container className="py-5">
<Row className="justify-content-center">
  <Col sm-8 className="text-center">لديك {person.length} مواعيد</Col>
</Row>

<Row className="justify-content-center my-2">
  <Col sm-8 className="">
    <div className="rectangle p-3">
      {person.length ? (person.map((item)=>{

        return (
        <div className="d-flex border-bottom mx-3 pb-3">
        <img className="img-avatar mb-2" src="person.jpg" alt="person"></img>
        <div className="px-3"><p className=""> {item.name}</p>
        <p className="fs-6 fw-normal">{item.date}</p>
        </div>
        </div>
        )
      })): null}

    </div>
  </Col>
</Row>

<Row className="justify-content-center my-2">
  <Col sm-8 className="d-flex justify-content-between">
    <Button className="btn-style pr-3">مسح الكل</Button>
    <Button className="btn-style pl-3">عرض الكل</Button>

  </Col>
</Row>

      </Container>
    </div>
  );
}

export default App;
